// src/pages/Testimonials/testimonials.styles.js

const styles = {
  section: {
    py: 6,
    backgroundColor: '#fff',
  },
  heading: {
    fontWeight: 600,
    color: '#0db1ad',
    textAlign: 'center',
    mb: 4,
  },
  loading: {
    display: 'flex',
    justifyContent: 'center',
    mt: 6,
  },
  errorBox: {
    maxWidth: 600,
    mx: 'auto',
    mb: 4,
  },
  featuredCarousel: {
    mb: 5,
  },
  allTestimonialsHeading: {
    mb: 3,
    fontWeight: 500,
  },
  emptyState: {
    textAlign: 'center',
    mt: 4,
    color: 'text.secondary',
  },
};

export default styles;
